export class Wizard{constructor(component_factory){this.factory=component_factory}hide(htmlElement){htmlElement.classList.remove("active");htmlElement.style.display="none"}toggle(htmlElement){if(htmlElement.classList.contains("active")){htmlElement.classList.remove("active");htmlElement.style.display="none"}else{htmlElement.classList.add("active");htmlElement.style.display=""}}hideUntil(htmlElement,seconds){if(seconds){if(typeof seconds==="number"){htmlElement.classList.remove("active");htmlElement.style.display="none";setTimeout(()=>{htmlElement.classList.add("active");htmlElement.style.display=""},seconds)}else{throw new Error(`seconds is Number not ${typeof seconds}`)}}else{throw new Error("Expected args not provided")}}show(htmlElement){htmlElement.classList.add("active");htmlElement.style.display=""}showUntil(htmlElement,seconds){if(seconds){if(typeof seconds==="number"){htmlElement.classList.add("active");htmlElement.style.display="";setTimeout(()=>{htmlElement.classList.remove("active");htmlElement.style.display="none"},seconds)}else{throw new Error(`seconds is Number not ${typeof seconds}`)}}else{throw new Error("Expected args not provided")}}switchTo(to_element,from_elements){if(to_element){this.show(to_element);for(let element of from_elements){this.hide(element)}}}};